// src/App.jsx

import React from 'react';
import { motion } from 'framer-motion';

export default function App() {
  const orbColors = ['#826BC6', '#5686C4', '#3EB489'];
  // Screen-wide roaming orbs (10 total)
  const orbs = Array.from({ length: 10 }).map((_, i) => {
    const size = Math.random() * 12 + 8; // between 8px and 20px
    const color = orbColors[i % orbColors.length];
    const leftPercent = Math.random() * 100; // across viewport width
    const topPercent = Math.random() * 80 + 15;  // start below header (15% to 95%)
    const offset = 50; // max movement in px
    const animateX = Math.random() * offset * 2 - offset;
    const animateY = Math.random() * offset * 2 - offset;
    const duration = Math.random() * 5 + 5;
    return (
      <motion.div
        key={i}
        className="absolute rounded-full"
        style={{
          width: size,
          height: size,
          backgroundColor: color,
          left: `${leftPercent}%`,
          top: `${topPercent}%`,
          opacity: 0.5,
          filter: 'drop-shadow(0 0 5px rgba(0,0,0,0.2))',
        }}
        animate={{ x: animateX, y: animateY, opacity: 0.5 }}
        transition={{ repeat: Infinity, repeatType: 'mirror', duration, ease: 'easeInOut' }}
      />
    );
  });

  return (
    <div className="relative flex flex-col min-h-screen font-sans overflow-hidden">
      {/* Full-screen orbs */}
      {orbs}

      {/* Header */}
      <header className="container mx-auto flex justify-between items-center px-8 py-4 z-10">
        <div className="flex items-center space-x-2 mt-2">
          <img src="/assets/logo.png" alt="PitchPal" className="h-8" />
          <span className="text-2xl font-bold text-black">PitchPal</span>
        </div>
        <nav className="flex items-center space-x-6 text-gray-700">
          <a href="/" className="hover:text-blue-900">홈</a>
          <a href="/services" className="hover:text-blue-900">서비스 소개</a>
          <a href="/team" className="hover:text-blue-900">팀원 소개</a>
          <a href="/history" className="hover:text-blue-900">History</a>
        </nav>
      </header>
      {/* Gradient underline below header (taller for smooth fade) */}
      <div className="w-full h-6 bg-gradient-to-b from-gray-300 to-transparent z-10" />

      {/* Hero Section with Orbs */}
      <main className="relative flex flex-1 items-center justify-between px-8 py-12 z-10">
        <div className="w-full lg:w-1/2 pr-8 space-y-6 ml-4 lg:ml-12">
          <motion.h1
            className="text-6xl font-bold leading-tight"
            initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 2 }}
          >
            <span className="block text-black">자신감 있는 발표의 시작,</span>
            <span className="block">
              <span className="bg-gradient-to-r from-[#826BC6] via-[#5686C4] to-[#3EB489] bg-clip-text text-transparent">PitchPal</span>과 함께하세요
            </span>
          </motion.h1>
          <p className="text-lg text-gray-700">
            언제 어디서든 AI가 실시간으로 발표를 분석하고 피드백을 제공합니다.<br />
            지금 바로 연습을 시작해보세요.
          </p>
        </div>
        <div className="relative inline-block transform -translate-x-[8rem] z-10">
          <img
            src="/assets/pitch.png"
            alt="발표 마이크"
            className="w-[25rem] rounded-lg"
          />
        </div>
      </main>

      {/* Bottom Buttons */}
      <div className="flex justify-center space-x-6 relative top-[-3rem] z-10">
        <a href="https://example.com/link1" className="flex items-center bg-white text-black px-40 py-3 rounded-lg shadow-[0_0_80px_rgba(216,178,216,0.5)] hover:shadow-[0_0_120px_rgba(216,178,216,0.5)] transition">
          <img src="/assets/note.png" alt="내용 분석 아이콘" className="w-8 h-8 mr-4" />
          <span className="text-xl font-medium">내용 분석</span>
        </a>
        <a href="https://example.com/link2" className="flex items-center bg-white text-black px-40 py-3 rounded-lg shadow-[0_0_80px_rgba(135,206,235,0.5)] hover:shadow-[0_0_120px_rgba(135,206,235,0.5)] transition">
          <img src="/assets/mic.png" alt="음성 분석 아이콘" className="w-8 h-8 mr-4" />
          <span className="text-xl font-medium">음성 분석</span>
        </a>
      </div>
    </div>
  );
}
