import React, { useState, useRef } from 'react';
import {
  Mic2,
  Clock,
  CheckCircle,
  Slash,
  PauseCircle,
  Activity,
  Volume2
} from 'lucide-react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  BarChart,
  Bar
} from 'recharts';

export default function AnalysisVoice() {
  const [file, setFile] = useState(null);
  const [progress, setProgress] = useState(0);
  const [stats, setStats] = useState({ speed: 0, accuracy: 0, fillerCount: 0, pauseAvg: 0 });
  const [speedData, setSpeedData] = useState([]);
  const [pitchAndVolumeData, setPitchAndVolumeData] = useState([]);
  const [fillerData, setFillerData] = useState([]);
  const [pauseData, setPauseData] = useState([]);
  const [tips, setTips] = useState([]);
  const fileInputRef = useRef(null);

  const handleFileSelect = e => {
    const selected = e.target.files[0];
    if (!selected) return;
    setFile(selected);
    setProgress(0);
    // TODO: 실제 API 연동
    const interval = setInterval(() => {
      setProgress(p => {
        const next = p + 10;
        if (next >= 100) { clearInterval(interval); return 100; }
        return next;
      });
    }, 200);
    // 예시 데이터 세팅
    setStats({ speed: 120, accuracy: 92, fillerCount: 5, pauseAvg: 1.2 });
    setSpeedData([
      { time: '0s', speed: 110 },
      { time: '10s', speed: 125 },
      { time: '20s', speed: 118 },
      { time: '30s', speed: 130 }
    ]);
    setPitchAndVolumeData([
      { x: 0, pitch: 200, volume: 0.8 },
      { x: 1, pitch: 220, volume: 0.7 },
      { x: 2, pitch: 210, volume: 0.85 }
    ]);
    setFillerData([
      { word: '음', count: 3 },
      { word: '어', count: 2 }
    ]);
    setPauseData([
      { length: 0.5, freq: 4 },
      { length: 1.2, freq: 2 },
      { length: 2.0, freq: 1 }
    ]);
    setTips([
      '말하기 속도가 약간 빠릅니다. 100–110 wpm으로 조절해 보세요.',
      '불필요 단어가 5회 발견되었습니다. 의식적으로 제거해 보세요.',
      '어간 공백이 평균 1.2초로 다소 깁니다. 0.5초 이하로 유지하세요.',
      '억양 변화를 적절히 활용해 발표를 생동감 있게 만드세요.'
    ]);
  };

  const handleReplay = () => console.log('재생');
  const handleDownload = () => console.log('다운로드');

  return (
    <div className="container mx-auto p-8 space-y-12">
      {/* 1. 음성 파일 업로드 */}
      <div className="max-w-xl mx-auto p-8 border border-gray-200 bg-white rounded-lg text-center">
        <Mic2 className="mx-auto mb-4 w-12 h-12 text-gray-400" />
        <h3 className="text-lg font-medium mb-2">음성 파일 업로드</h3>
        <p className="text-sm text-gray-500 mb-4">.mp3, .wav, .ogg, .m4a 지원</p>
        <input
          type="file"
          ref={fileInputRef}
          accept=".mp3,.wav,.ogg,.m4a"
          className="hidden"
          onChange={handleFileSelect}
        />
        <button
          onClick={() => fileInputRef.current?.click()}
          className="px-6 py-3 rounded-full border border-gray-300 hover:bg-gray-100 transition"
        >파일 선택</button>
      </div>

      {/* 2. 분석 진행 상태 */}
      {file && (
        <div className="max-w-xl mx-auto">
          <progress value={progress} max="100" className="w-full h-2 mb-2 accent-[#5686C4]" />
          <p className="text-sm text-gray-600">음성 분석 중… {progress}%</p>
        </div>
      )}

      {/* 3. 요약 대시보드 */}
      {progress === 100 && (
        <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-6 gap-6">
          {/* stats cards unchanged */}
        </div>
      )}

      {/* 4. 시각화 차트 섹션 */}
      {progress === 100 && (
        <section className="mt-12 grid grid-cols-4 gap-4">
          <div className="p-4 bg-white border border-gray-100 rounded-lg">
            <h4 className="text-md font-semibold text-[#5686C4] mb-2">속도 변화</h4>
            <LineChart width={200} height={120} data={speedData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <RechartsTooltip />
              <Line type="monotone" dataKey="speed" stroke="#5686C4" strokeWidth={2} />
            </LineChart>
          </div>
          <div className="p-4 bg-white border border-gray-100 rounded-lg">
            <h4 className="text-md font-semibold text-[#5686C4] mb-2">억양·음량</h4>
            <LineChart width={200} height={120} data={pitchAndVolumeData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="x" />
              <YAxis yAxisId="left" />
              <YAxis yAxisId="right" orientation="right" />
              <RechartsTooltip />
              <Line yAxisId="left" type="monotone" dataKey="pitch" stroke="#826BC6" strokeWidth={2} />
              <Line yAxisId="right" type="monotone" dataKey="volume" stroke="#3EB489" strokeWidth={2} />
            </LineChart>
          </div>
          <div className="p-4 bg-white border border-gray-100 rounded-lg">
            <h4 className="text-md font-semibold text-[#5686C4] mb-2">불필요 단어</h4>
            <BarChart width={200} height={120} data={fillerData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="word" />
              <YAxis />
              <RechartsTooltip />
              <Bar dataKey="count" fill="#5686C4" barSize={20} />
            </BarChart>
          </div>
          <div className="p-4 bg-white border border-gray-100 rounded-lg">
            <h4 className="text-md font-semibold text-[#5686C4] mb-2">어간 공백 길이</h4>
            <BarChart width={200} height={120} data={pauseData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="length" />
              <YAxis />
              <RechartsTooltip />
              <Bar dataKey="freq" fill="#5686C4" barSize={20} />
            </BarChart>
          </div>
        </section>
      )}

      {/* 5. 개선 제안 & 팁 */}
      {progress === 100 && (
        <section className="mt-12 bg-white p-8 border border-gray-100 rounded-lg">
          <h3 className="text-xl font-bold text-[#826BC6] mb-4">개선 제안</h3>
          <ul className="list-disc pl-6 space-y-2 text-gray-700">
            {tips.map((tip, i) => (
              <li key={i}>{tip}</li>
            ))}
          </ul>
        </section>
      )}

      {/* 6. 재생·내보내기 */}
      {progress === 100 && (
        <div className="flex justify-end space-x-4 mt-8">
          <button
            onClick={handleReplay}
            className="px-6 py-3 border border-gray-300 rounded-lg hover:bg-gray-100 transition"
          >다시 듣기</button>
          <button
            onClick={handleDownload}
            className="px-6 py-3 bg-[#3EB489] text-white rounded-lg hover:bg-[#36A778] transition"
          >보고서 다운로드</button>
        </div>
      )}
    </div>
  );
}
