import React, { useState, useRef } from 'react';
import { Video, Camera, Eye, Smile, Target } from 'lucide-react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer
} from 'recharts';

export default function AnalysisVideo() {
  const [fileUrl, setFileUrl] = useState(null);
  const [progress, setProgress] = useState(0);
  const [angleData, setAngleData] = useState([]);
  const [blinkData, setBlinkData] = useState([]);
  const [expressionData, setExpressionData] = useState([]);
  const [gazeZoneData, setGazeZoneData] = useState([]);
  const [tips, setTips] = useState([]);
  const fileInputRef = useRef(null);

  const handleFileSelect = e => {
    const file = e.target.files[0];
    if (!file) return;
    setFileUrl(URL.createObjectURL(file));
    setProgress(0);
    const interval = setInterval(() => {
      setProgress(p => {
        const next = p + 10;
        if (next >= 100) { clearInterval(interval); return 100; }
        return next;
      });
    }, 200);
    // placeholder data
    setAngleData([{ angle: -5, freq: 3 },{ angle: 0, freq: 10 },{ angle: 5, freq: 2 }]);
    setBlinkData([{ time: '0s', blinks: 2 },{ time: '10s', blinks: 3 },{ time: '20s', blinks: 1 }]);
    setExpressionData([{ expr: 'Neutral', value: 40 },{ expr: 'Smile', value: 35 },{ expr: 'Frown', value: 25 }]);
    setGazeZoneData([{ zone: 'Left', value: 30 },{ zone: 'Center', value: 50 },{ zone: 'Right', value: 20 }]);
    setTips([
      '머리 각도가 평균 ±3°보다 큽니다. 정면을 유지해 보세요.',
      '눈 깜빡임이 15회/분으로 다소 빈번합니다.',
      '표정 변화가 활발하여 주의 집중이 분산될 수 있습니다.',
      '시선 집중도가 82%로, 시선 분산 구간을 줄이세요.'
    ]);
  };

  const handleReplay = () => document.querySelector('video')?.play();
  const handleDownload = () => console.log('보고서 다운로드');

  return (
    <div className="container mx-auto p-8 space-y-12">
      {/* 1. 영상 파일 업로드 */}
      <div className="max-w-xl mx-auto p-8 border border-gray-200 bg-white rounded-lg text-center">
        <Video className="mx-auto mb-4 w-12 h-12 text-gray-400" />
        <h3 className="text-lg font-medium mb-2">영상 파일 업로드</h3>
        <p className="text-sm text-gray-500 mb-4">.mp4, .mov, .avi 지원</p>
        <input
          type="file"
          ref={fileInputRef}
          accept=".mp4,.mov,.avi"
          className="hidden"
          onChange={handleFileSelect}
        />
        <button
          onClick={() => fileInputRef.current?.click()}
          className="px-6 py-3 rounded-full border border-gray-300 hover:bg-gray-100 transition"
        >
          파일 선택
        </button>
      </div>

      {/* 2. 분석 진행 상태 */}
      {fileUrl && progress < 100 && (
        <div className="max-w-xl mx-auto">
          <progress
            value={progress}
            max="100"
            className="w-full h-2 mb-2 accent-[#5686C4]"
          />
          <p className="text-sm text-gray-600">영상 분석 중… {progress}%</p>
        </div>
      )}

      {progress === 100 && (
        <>
          {/* 3. 요약 대시보드 */}
          <div className="grid grid-cols-2 sm:grid-cols-4 gap-6">
            <div className="p-6 border rounded-lg bg-white text-center">
              <Camera className="mx-auto mb-2 w-8 h-8 text-[#5686C4]" />
              <p className="text-2xl font-bold">±3°</p>
              <p className="text-gray-500">각도 편차</p>
            </div>
            <div className="p-6 border rounded-lg bg-white text-center">
              <Eye className="mx-auto mb-2 w-8 h-8 text-[#5686C4]" />
              <p className="text-2xl font-bold">15회/분</p>
              <p className="text-gray-500">눈 깜빡임 빈도</p>
            </div>
            <div className="p-6 border rounded-lg bg-white text-center">
              <Smile className="mx-auto mb-2 w-8 h-8 text-[#5686C4]" />
              <p className="text-2xl font-bold">Moderate</p>
              <p className="text-gray-500">표정 변화</p>
            </div>
            <div className="p-6 border rounded-lg bg-white text-center">
              <Target className="mx-auto mb-2 w-8 h-8 text-[#5686C4]" />
              <p className="text-2xl font-bold">82%</p>
              <p className="text-gray-500">시선 집중도</p>
            </div>
          </div>

          {/* 4. 영상 플레이어 & 히트맵 */}
          <div className="flex flex-col lg:flex-row gap-8">
            <video
              controls
              className="w-full lg:w-1/2 rounded-lg shadow"
              src={fileUrl}
            />
            <div className="w-full lg:w-1/2 bg-white border rounded-lg p-4">
              <h4 className="text-lg font-semibold text-[#5686C4] mb-2">
                시선 히트맵
              </h4>
              <div className="w-full h-64 bg-gray-100 flex items-center justify-center">
                Heatmap Placeholder
              </div>
            </div>
          </div>

          {/* 5. 상세 차트 섹션 (2×2 레이아웃) */}
          <section className="mt-12 grid grid-cols-1 sm:grid-cols-2 gap-6 justify-items-center">
            {/* 각도 분포 */}
            <div className="p-6 bg-white border border-gray-100 rounded-lg w-full max-w-md">
              <h4 className="text-lg font-semibold text-[#5686C4] mb-4 text-center">
                각도 분포
              </h4>
              <ResponsiveContainer width="100%" height={200}>
                <BarChart data={angleData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="angle" />
                  <YAxis />
                  <RechartsTooltip />
                  <Bar dataKey="freq" fill="#5686C4" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            {/* 눈 깜빡임 */}
            <div className="p-6 bg-white border border-gray-100 rounded-lg w-full max-w-md">
              <h4 className="text-lg font-semibold text-[#3EB489] mb-4 text-center">
                눈 깜빡임
              </h4>
              <ResponsiveContainer width="100%" height={200}>
                <LineChart data={blinkData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis />
                  <RechartsTooltip />
                  <Line
                    type="monotone"
                    dataKey="blinks"
                    stroke="#3EB489"
                    strokeWidth={2}
                  />
                </LineChart>
              </ResponsiveContainer>
            </div>

            {/* 표정 변화 */}
            <div className="p-6 bg-white border border-gray-100 rounded-lg w-full max-w-md">
              <h4 className="text-lg font-semibold text-[#826BC6] mb-4 text-center">
                표정 변화
              </h4>
              <ResponsiveContainer width="100%" height={200}>
                <PieChart>
                  <Pie
                    data={expressionData}
                    dataKey="value"
                    nameKey="expr"
                    innerRadius={40}
                    outerRadius={80}
                    label
                  >
                    {expressionData.map((entry, index) => (
                      <Cell
                        key={`cell-${index}`}
                        fill={['#826BC6', '#5686C4', '#3EB489'][index % 3]}
                      />
                    ))}
                  </Pie>
                  <RechartsTooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>

            {/* 시선 분포 */}
            <div className="p-6 bg-white border border-gray-100 rounded-lg w-full max-w-md">
              <h4 className="text-lg font-semibold text-[#5686C4] mb-4 text-center">
                시선 분포
              </h4>
              <ResponsiveContainer width="100%" height={200}>
                <PieChart>
                  <Pie
                    data={gazeZoneData}
                    dataKey="value"
                    nameKey="zone"
                    innerRadius={40}
                    outerRadius={80}
                    label
                  >
                    {gazeZoneData.map((entry, index) => (
                      <Cell
                        key={`cell-${index}`}
                        fill={['#5686C4', '#36A778', '#826BC6'][index % 3]}
                      />
                    ))}
                  </Pie>
                  <RechartsTooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </section>

          {/* 6. 개선 제안 & 다운로드 */}
          <section className="mt-12 bg-white p-8 border border-gray-100 rounded-lg">
            <h3 className="text-xl font-bold text-[#826BC6] mb-4">개선 제안</h3>
            <ul className="list-disc pl-6 space-y-2 text-gray-700">
              {tips.map((tip, i) => (
                <li key={i}>{tip}</li>
              ))}
            </ul>
          </section>
          <div className="flex justify-end space-x-4 mt-8">
            <button
              onClick={handleReplay}
              className="px-6 py-3 border border-gray-300 rounded-lg hover:bg-gray-100 transition"
            >
              다시 보기
            </button>
            <button
              onClick={handleDownload}
              className="px-6 py-3 bg-[#3EB489] text-white rounded-lg hover:bg-[#36A778] transition"
            >
              보고서 다운로드
            </button>
          </div>
        </>
      )}
    </div>
  );
}
